mapper.select:
SELECT count, p.* FROM product p LEFT OUTER JOIN (SELECT product_unum, count(*) count FROM payment GROUP BY product_unum) v ON p.product_unum = v.product_unum WHERE p.category_unum IN (SELECT c.category_unum FROM category c
WHERE category_ref = #{categoryUnum}
) ORDER BY NVL(count, 0) DESC, p.product_regdate DESC, product_name
