mapper.insert:
INSERT INTO reply(
	  payment_unum
	, reply_content

	, reply_image01_path

) VALUES(
	  #{paymentUnum}
	, #{replyContent}

	, #{replyImage01Path}

)

mapper.update:
UPDATE   reply
   SET   reply_content = #{replyContent}

   	   , reply_image01_path = #{replyImage01Path}

	   , reply_regdate = SYSDATE
 WHERE   reply_unum = #{replyUnum}
